const now = new Date();
const day = now.getDate();
const daysInMonth = new Date(now.getFullYear(), now.getMonth() + 1, 0).getDate();
const monthProgress = ((day / daysInMonth) * 100).toFixed(2);

return [{
  json: {
    date: now.toISOString().split('T')[0],
    day,
    daysInMonth,
    monthProgress
  }
}];

